<?phpnamespace App\Models\Traits;use Illuminate\Support\Facades\App;trait Translatable{    protected $defaultLocale = 'ru';    public function __($originFieldName) {        $locale = App::getLocale();        if($locale !== $this->defaultLocale) {            $fieldName = $originFieldName. '_'.$locale;        }        else {            $fieldName = $originFieldName;        }        if(in_array($fieldName,array_keys($this->attributes)) && (is_null($this->$fieldName) || empty($this->$fieldName))) {            return $this->$originFieldName;        }        else if(in_array($fieldName,array_keys($this->attributes))) {            return $this->$fieldName;        }        else {            throw new \LogicException('no such attribute for model ' .get_class($this));        }    }}