<div class="product_labels">    @if($sku->product->isNew())    <span class="badge badge-success">        New    </span>    @endif    @if($sku->product->isSale())    <span class="badge badge-warning">        Sale    </span>    @endif    @if($sku->product->isHot())    <span class="badge badge-danger">        Hot    </span>    @endif</div>