    <div class="modal-content">        <div class="modal-header justify-content-center bg-dark">            <h5 class="modal-title" id="add-to-cartCenterTitle"> <span class="ion-checkmark-round"></span>                Выберите торговое предложение, которое хотите приобрести</h5>            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close">            </button>        </div>        <div class="modal-body">            <div class="row">                <div class="col-lg-5 divide-right">                    <div class="row">                        <div class="col-md-6">                            <img src="{{ Storage::url($product->picture) }}" alt="{{ $product->__('name') }}">                        </div>                    <div class="col-md-6 mb-2 mb-md-0">                        <h4 class="product-name">{{ $product->__('name') }}</h4>                        <h5 class="price"> <span class="price-value">{{ $product->skus->first()->price }}</span> {{ $currencySymbol }}</h5>                        <div class="product-properties">                            @foreach($product->skus_properties as $prop_name => $property)                                <div class="product-size d-flex align-items-center mt-30">                                    <h6 class="title">                                        <select name="{{ $prop_name }}" id="{{ $property['prop_id'] }}">                                            @foreach($property['values'] as $value)                                                <option value="{{ $value['id'] }}" {{ $value['available'] === true ? '' : 'disabled' }}>{{ $value['name'] }}</option>                                            @endforeach                                        </select>                                    </h6>                                </div>                            @endforeach                        </div>                        <h6 class="quantity"><strong>Количество:</strong>&nbsp;1</h6>                    </div>                </div>            </div>            <div class="col-lg-7">                <div class="modal-cart-content">                    <p class="cart-products-count">В вашей корзине n товаров(доделать!)</p>                    <p><strong>Общая сумма товаров:</strong>&nbsp;140 {{$currencySymbol}} (доделать!!)</p>                    <div class="cart-content-btn">                        <button type="button" class="btn theme-btn--dark1 btn--md mt-4"                                data-bs-dismiss="modal">Продолжить покупки</button>                        <form class="add-to-basket" action="{{ route('basketAdd',$product->skus->first()->id) }}" method="POST">                            @csrf                            <button class="btn theme-btn--dark1 btn--md mt-4"><i                                        class="ion-checkmark-round" type="submit"></i>Добавить в корзину</button>                        </form>                    </div>                </div>            </div>        </div>    </div></div>    <script>        $('.product-properties select').change(function() {            let prepareData = {};            $('.product-properties select').each(function() {                prepareData[$(this).attr('id')] = $(this).val();            })            axios.get('/basket/modal/get-sku',{                params: {                    data: prepareData,                    product_id: {{ $product->id }}                }            })            .then((response) => {                $('h5 .price-value').html(response.data.SKU.price);                let actionHref = $('.add-to-basket').attr('action');                $('.add-to-basket').attr('action',actionHref.replaceAll(/(\d)+$/g,response.data.SKU.id));                let productData = response.data.PRODUCT;                Object.keys(productData).forEach(function(key) {                    let item = productData[key];                    let select = $('select[id=' + item["prop_id"] + ']')                    Object.keys(item["values"]).forEach(function(keyValue) {                        let prop = item["values"][keyValue];                        let option = select.find('option[value=' + prop.id + ']');                        if(prop['available'] && option.attr('disabled')) {                            option.removeAttr('disabled');                        }                        else if(!prop['available'] && !option.attr('disabled')) {                            option.attr('disabled','disabled');                        }                    });                });            })        })    </script>